mapper.select:
select ari.id,ari.role_bid,ari.`identity`, ari.`type`,ari.created_time,ari.created_by, ari.tenant_id,ari.enable_flag,ari.delete_flag, ari.name, ari.dept_name,ari.input_percentage, ar.code as roleCode, ar.name as roleName from apm_role ar inner join apm_role_identity ari on ar.bid = ari.role_bid and ar.delete_flag = 0 and ari.delete_flag = 0 where ar.sphere_bid = #{sphereBid} and ari.type = #{type}
and ari.name like concat('%',#{name},'%')
order by ari.created_time

mapper.delete:
delete from apm_role_identity

    delete_flag = 0

        and id = #{roleIdentityDto.id}


        and foreign_bid = #{roleIdentityDto.foreignBid}


        and role_bid in

            #{roleBid}
