mapper.select:
SELECT

FROM cfg_life_cycle_state


        AND enable_flag=#{lifeCycleStatusListQo.enableFlag,jdbcType=BIGINT}


        AND name like  concat(concat('%',#{lifeCycleStatusListQo.name}),'%')


        AND group_code=#{lifeCycleStatusListQo.groupCode,jdbcType=VARCHAR}


        AND updated_time >= #{lifeCycleStatusListQo.startDate,jdbcType=TIMESTAMP}



        AND updated_time <= #{lifeCycleStatusListQo.endDate,jdbcType=TIMESTAMP}



order by updated_time desc,id asc

SELECT
FROM cfg_life_cycle_state where code IN
#{code,jdbcType=VARCHAR}
