mapper.select:
select 
from object_relation_attr where 1 = 1

     and relation_bid = #{ao.relationBid,jdbcType=VARCHAR}


    and  inner_name = #{ao.innerName,jdbcType=VARCHAR}


    and  source_model = #{ao.sourceModel,jdbcType=VARCHAR}


    and source_model_code = #{ao.sourceModelCode,jdbcType=VARCHAR}

mapper.insert:
insert into object_relation_attr ( id,bid,relation_bid ,inner_name,`explain`,source_model,source_model_code, delete_flag,created_time,updated_time, created_by,updated_by,app_id, company_id,column_width,real_use_in_view, real_relative_sort,use_in_query
) values
(#{item.id,jdbcType=INTEGER}, #{item.bid,jdbcType=VARCHAR}, #{item.relationBid,jdbcType=VARCHAR}, #{item.innerName,jdbcType=VARCHAR},#{item.explain,jdbcType=VARCHAR}, #{item.sourceModel,jdbcType=VARCHAR}, #{item.sourceModelCode,jdbcType=VARCHAR}, #{item.isDelete,jdbcType=TINYINT}, #{item.createdTime,jdbcType=TIMESTAMP}, #{item.updatedTime,jdbcType=TIMESTAMP}, #{item.createdBy,jdbcType=VARCHAR}, #{item.updatedBy,jdbcType=VARCHAR}, #{item.appId,jdbcType=VARCHAR},#{item.companyId,jdbcType=INTEGER},#{item.columnWidth,jdbcType=INTEGER}, #{item.realUseInView,jdbcType=TINYINT},#{item.realRelativeSort,jdbcType=INTEGER},#{item.useInQuery,jdbcType=INTEGER})

mapper.delete:
delete from object_relation_attr where relation_bid in (

    #{item}

)
